import React, { useState } from "react";
import {Navbar, Nav} from "react-bootstrap";
import { BiHome, BiUser, BiFileBlank, BiBookOpen, BiBookContent} from "react-icons/bi"

function CustomNav(props){

    const {
        contactSelected,
        setContactSelected,
        aboutSelected,
        setAboutSelected,
        portfolioSelected,
        setPortfolioSelected,
        resumeSelected,
        setResumeSelected
    } = props;

    function handleClick(target){
      setContactSelected(false);
      setPortfolioSelected(false);
      setAboutSelected(false);
      setResumeSelected(false);

      if (target===0){
        setContactSelected(true);
      }
      if (target===1){
        setAboutSelected(true);
      }
      if(target===2){
        setPortfolioSelected(true);
      }
      if(target===3){
        setResumeSelected(true);
      }
    }

  const [isNotShown, setIsNotShown] = useState(true);

  return (
    <header>
      <Navbar className="nav-menu">
        <ul>
          <li 
          onMouseEnter={() => setIsShown(false)}
          onMouseLeave={()=> setIsShown(true)}>
            {isShown ? (
              <>
              <Nav.Link><BiHome/></Nav.Link>
              </>
            ):(<><Nav.Link onClick={() => handleClick(1)}><BiBookContent/><span>Home</span> </Nav.Link></>)}

          </li>
          <li>
            {contactSelected ? (
                  <><Nav.Link id="nav-selected"><BiBookContent/><span>Contact</span></Nav.Link></>
                ) : (
                  <><Nav.Link onClick={() => handleClick(0)}><BiBookContent/><span>Contact</span> </Nav.Link></>
                )
            }
          </li>
          <li>
            {aboutSelected ? (
                  <><Nav.Link id="nav-selected"><BiUser/><span>About Me</span></Nav.Link></>
                ) : (
                  <><Nav.Link onClick={() => handleClick(1)}><BiUser/><span>About Me</span> </Nav.Link></>
                )
            }
          </li>
          <li>
            {portfolioSelected ? (
                  <><Nav.Link id="nav-selected"><BiFileBlank/><span>Portfolio</span></Nav.Link></>
                ) : (
                  <><Nav.Link onClick={() => handleClick(2)}><BiFileBlank/><span>Portfolio</span> </Nav.Link></>
                )
            }
          </li>
          <li>
            {resumeSelected ? (
                  <><Nav.Link id="nav-selected"><BiBookOpen/><span>Resume</span></Nav.Link></>
                ) : (
                  <><Nav.Link onClick={() => handleClick(3)}><BiBookOpen/><span>Resume</span> </Nav.Link></>
                )
            }
          </li>
          </ul>
      </Navbar>
    </header>
  )}

export default CustomNav;
