import React from "react";
import {Navbar, Nav, Container} from "react-bootstrap";
import { BiHome, biUser, biFileBlank, biBookOpen, biBookContent} from "react-icons/bi"

function CustomNav(props){

    const {
        contactSelected,
        setContactSelected,
        aboutSelected,
        setAboutSelected,
        portfolioSelected,
        setPortfolioSelected,
        resumeSelected,
        setResumeSelected
    } = props;

    function handleClick(target){
      setContactSelected(false);
      setPortfolioSelected(false);
      setAboutSelected(false);
      setResumeSelected(false);

      if (target===0){
        setContactSelected(true);
      }
      if (target===1){
        setAboutSelected(true);
      }
      if(target===2){
        setPortfolioSelected(true);
      }
      if(target===3){
        setResumeSelected(true);
      }
    }

  return (
    <header>
      <Navbar className="nav-menu">
        <ul>
          <li>
            <Nav.Link onClick={()=>handleClick(1)}><BiHome/><span>Home</span></Nav.Link>
          </li>
          <li>
            {contactSelected ? (
                  <><Nav.Link id="nav-selected"><i className="bx bx-home"></i><span>Contact</span></Nav.Link></>
                ) : (
                  <><Nav.Link onClick={() => handleClick(0)}> <i className="bx bx-home"></i><span>Contact</span> </Nav.Link></>
                )
            }
          </li>
          <li>
            {aboutSelected ? (
                  <><Nav.Link id="nav-selected"><BiUser/><span>About Me</span></Nav.Link></>
                ) : (
                  <><Nav.Link onClick={() => handleClick(1)}> <i className="bx bx-home"></i><span>About Me</span> </Nav.Link></>
                )
            }
          </li>
          <li>
            {portfolioSelected ? (
                  <><Nav.Link id="nav-selected"><i className="bx bx-home"></i><span>Portfolio</span></Nav.Link></>
                ) : (
                  <><Nav.Link onClick={() => handleClick(2)}> <i className="bx bx-home"></i><span>Portfolio</span> </Nav.Link></>
                )
            }
          </li>
          <li>
            {resumeSelected ? (
                  <><Nav.Link id="nav-selected"><i className="bx bx-home"></i><span>Resume</span></Nav.Link></>
                ) : (
                  <><Nav.Link onClick={() => handleClick(3)}> <i className="bx bx-home"></i><span>Resume</span> </Nav.Link></>
                )
            }
          </li>
          </ul>
      </Navbar>
    </header>
  )}

export default CustomNav;
