import React, {useState} from "react";
import {Container, Card, Button} from "react-bootstrap";

function Portfolio(){

    const [work] = useState([
        {
            name: 'Gopher',
            description: 'Node.js, etc',
            link: 'https://github.com/lrk83',
            photo: "../assets/images/96860-groundhog-day-cartoon-gopher-for-events-near-me.png"
        }
    ])

    return(
        <div>
            <Container>
                {work.map((example, i)=>(
                    <Card style={{ width: '18rem' }} key={work.name}>
                        <Card.Img variant="top" src={require(work.photo)} />
                        <Card.Body >
                        <Card.Text>
                            {example.description}
                        </Card.Text>
                        <Button variant="primary" href={example.link}>{example.name}</Button>
                        </Card.Body>
                    </Card>
                ))}
            </Container>
        </div>
    )
}

export default Portfolio;
